import { PieceType, TeamType, Piece } from "../constants"
import { isPawnNormalMove, isPawnAttackMove, isPawnStartingMove, isFriendlyPiece, unsetEnPassant, setEnPassant, } from "./pawnLogic"
import { isLegalBishopMove, isLegalRookMove, isLegalQueenMove, isLegalKingMove, isLegalNightMove} from "./pieceLogic"

export default class Referee{
    takePiece(x : number, y: number, team : TeamType, boardState : Piece []){
        const piece = boardState.find(p => p.x === x && p.y === y && p.team !== team)  
        if (piece) {
            piece.taken = true
        }
    }
    endMove(x : number, y: number, team : TeamType, boardState : Piece [], valid : any){
        if (!isFriendlyPiece(x, y, team, boardState)){
            this.takePiece(x, y, team, boardState)
            unsetEnPassant(boardState)
            valid.value = true          
        }
    }
    isValidMove(px : number, py : number, x : number, y : number, type : PieceType, 
                team : TeamType, boardState : Piece [], activePlayer : TeamType){
        // Only the active player can move
        if (team !== activePlayer){
            return false
        }
        // A non-move is not a move
        if (px === x && py === y){
            return false
        }
        const enemyTeam = team === TeamType.WHITE ? TeamType.BLACK : TeamType.WHITE
        var valid = { value: false }
        const dx = x - px
        const dy = y - py
        if (type === PieceType.PAWN){
            const startingRow = team === TeamType.WHITE ? 1 : 6
            const increment = team === TeamType.WHITE ? 1 : - 1
            if (isPawnStartingMove(px, py, x, y, team, boardState, increment)){
                setEnPassant(px, py, boardState)
                return true
            }
            if (isPawnNormalMove(px, py, x, y, team, boardState, increment)){
                this.endMove(x, y, team, boardState, valid)
            }
            if (isPawnAttackMove(px, py, x, y, team, boardState, increment)){
                this.endMove(x, y, team, boardState, valid)
            }
        }
        // NIGHT LOGIC 
        if (type === PieceType.NIGHT){
            if (isLegalNightMove(px, py, dx, dy, boardState)){
                this.endMove(x, y, team, boardState, valid)
            }
        }
        // BISHOP LOGIC
        if (type === PieceType.BISHOP){
            if (isLegalBishopMove(px, py, dx, dy, boardState)){
                    this.endMove(x, y, team, boardState, valid)
            }
        }
        // ROOK LOGIC
        if (type === PieceType.ROOK){
            if (isLegalRookMove(px, py, dx, dy, boardState)){
                this.endMove(x, y, team, boardState, valid)
            }
         }
        // QUEEN LOGIC
        if (type === PieceType.QUEEN){
            if (isLegalQueenMove(px, py, dx, dy, boardState)){
                this.endMove(x, y, team, boardState, valid)
            }
         }
         // KING LOGIC
        if (type === PieceType.KING){
            if (isLegalKingMove(px, py, dx, dy, boardState)){
                this.endMove(x, y, team, boardState, valid)
            }
         }
    return valid.value
    }
}
