import { PieceType, TeamType, Piece } from "../../constants"
import { isLegalBishopMove, isLegalRookMove, isLegalQueenMove, isLegalKingMove, isLegalNightMove} from "../../referee/pieceLogic"

export default function isMyKingInCheck(team : TeamType, boardState : Piece []){
    const enemyTeam = team === TeamType.WHITE ? TeamType.BLACK : TeamType.WHITE
    const myKing = boardState.find(p => p.team === team && p.type === PieceType.KING) 
    boardState.forEach(p => {
        if (myKing && p.team === enemyTeam) {
            console.log('yo')
            const dx = myKing.x - p.x
            const dy = myKing.y - p.y
            switch (p.type){
                case PieceType.NIGHT : {
                    if (isLegalNightMove(p.x, p.y, dx, dy, boardState)){
                        return true;
                    } break;
                }
                case PieceType.BISHOP : {
                    if (isLegalBishopMove(p.x, p.y, dx, dy, boardState)){
                        return true;
                    } break;
                }
                case PieceType.ROOK : {
                    if (isLegalRookMove(p.x, p.y, dx, dy, boardState)){
                        return true;
                    } break;
                }
                case PieceType.QUEEN : {
                    if (isLegalQueenMove(p.x, p.y, dx, dy, boardState)){
                        return true;
                    } break;
                }
                case PieceType.KING : {
                    if (isLegalKingMove(p.x, p.y, dx, dy, boardState)){
                        return true;
                    } break;
                }
            }

        }
    })
    return false

}
