import './TakenPiece.css'
import { TeamType, Piece, PieceType, HORIZONTAL_AXIS, VERTICAL_AXIS } from '../../constants'
import { useState } from 'react';
import SuperPiece from '../../superPiece';



interface Props {
    piece : Piece
}





export default function TakenPiece({piece} : Props) {
    const [chosen, setChosen] = useState<boolean>(true)
    const top = piece.SuperPiece.team === TeamType.WHITE ? '91vh' : '04vh';
    const left = piece.SuperPiece.team === TeamType.WHITE ? '21vw' : '21vw';
    const x = HORIZONTAL_AXIS[piece.x]
    const y = VERTICAL_AXIS[piece.y]
    const buttons = get_buttons(piece)

    function get_buttons(piece : Piece){
        let buttons = []
        const dir = piece.SuperPiece.team === TeamType.WHITE ? 'white_pieces' : 'black_pieces';
        const qURL = `assets/${dir}/q.png`
        const bURL = `assets/${dir}/b.png`
        const nURL = `assets/${dir}/n.png`
        const rURL = `assets/${dir}/r.png`
        const pURL = `assets/${dir}/p.png`
        if (piece.SuperPiece.subPieces.includes(PieceType.QUEEN)){
            buttons.push(<button className = 'take-button' style = {{backgroundImage : `url(${qURL})`}} onClick = {takeQueen}></button>)
        }
        if (piece.SuperPiece.subPieces.includes(PieceType.ROOK)){
            buttons.push(<button className = 'take-button' style = {{backgroundImage : `url(${rURL})`}} onClick = {takeRook}></button>)
        }
        if (piece.SuperPiece.subPieces.includes(PieceType.BISHOP)){
            buttons.push(<button className = 'take-button' style = {{backgroundImage : `url(${bURL})`}} onClick = {takeBishop}></button>)
        }
        if (piece.SuperPiece.subPieces.includes(PieceType.NIGHT)){
            buttons.push(<button className = 'take-button' style = {{backgroundImage : `url(${nURL})`}} onClick = {takeNight}></button>)
        }
        if (piece.SuperPiece.subPieces.includes(PieceType.PAWN)){
            buttons.push(<button className = 'take-button' style = {{backgroundImage : `url(${pURL})`}} onClick = {takePawn}></button>)
        }
        return buttons
    }


    function takeQueen(){
        setChosen(false)
    }
    function takeRook(){
        setChosen(false)    
    }
    function takeBishop(){
        setChosen(false)       
    }
    function takeNight(){
        setChosen(false)    
    }
    function takePawn(){
        setChosen(false)
    }
    if (chosen){  
    return <span className = 'pop' style = {{top : top, left: left}}> 
    <span className = 'white-text'>Your piece was taken on {x}{y} what piece was it? </span>
    {buttons}
    </span>
    }
    else {
        return <div></div>
    }
}
