import './TakenPiece.css'
import { TeamType } from '../../constants'
import { useState } from 'react';



interface Props {
    team : TeamType | null
}

export default function TakenPiece({team} : Props) {
    const [chosen, setChosen] = useState<boolean>(true)
    const dir = team === TeamType.WHITE ? 'white_pieces' : 'black_pieces';
    const top = team === TeamType.WHITE ? '91vh' : '09vh';
    const left = team === TeamType.WHITE ? '21vw' : '21vw';
    const qURL = `assets/${dir}/q.png`
    const bURL = `assets/${dir}/b.png`
    const nURL = `assets/${dir}/n.png`
    const rURL = `assets/${dir}/r.png`
    const pURL = `assets/${dir}/p.png`

    function takeQueen(){
        setChosen(false)
    }
    function takeRook(){
        setChosen(false)    
    }
    function takeBishop(){
        setChosen(false)       
    }
    function takeNight(){
        setChosen(false)    
    }
    function takePawn(){
        setChosen(false)
    }
    if (chosen){  
    return <span className = 'pop' > 
    <span className = 'white-text'>Your piece was taken on h3 what piece was it?   </span>
    <button className = 'take-button' style = {{backgroundImage : `url(${qURL})`}} onClick = {takeQueen}></button>
    <button className = 'take-button' style = {{backgroundImage : `url(${rURL})`}} onClick = {takeRook}></button>
    <button className = 'take-button' style = {{backgroundImage : `url(${bURL})`}} onClick = {takeBishop}></button>
    <button className = 'take-button' style = {{backgroundImage : `url(${nURL})`}} onClick = {takeNight}></button>
    <button className = 'take-button' style = {{backgroundImage : `url(${pURL})`}} onClick = {takePawn}></button>
    </span>
    }
    else {
        return <div></div>
    }
}
