import './PopUp.css'
import React, {useRef, useState, } from 'react';

interface Props {
    trigger? : boolean;
}


export default function PopUp({trigger} : Props){
    const [display, setDisplay] = useState('Congratulations You Promoted')

    function giveQueen(){
        setDisplay('You Chose Queen')  
    }
    return trigger ? 
    <div className = 'popUp'> 
    <h3> {display} </h3> 
    <button onClick = {giveQueen}>Queen Me</button>
    </div> : <div/>

}
