import './PopUp.css'
import React, {useRef, useState, } from 'react';
import { PromotionSquare, TeamType } from '../../constants'



export default function PopUp(team : TeamType){
    const [display, setDisplay] = useState('Congratulations You Promoted')

    const wb = team === TeamType.WHITE ? 'w' : 'b';
    const wqURL = `assets/images/queen_${wb}.png`
    const wbURL = `assets/images/bishop_${wb}.png`
    const wnURL = `assets/images/night_${wb}.png`
    const wrURL = `assets/images/rook_${wb}.png`

    function giveQueen(){
        setDisplay('You Chose Queen')  
    }
    function giveNight(){
        setDisplay('You Chose Night')  
    }
    function giveBishop(){
        setDisplay('You Chose Bishop')  
    }
    function giveRook(){
        setDisplay('You Chose Rook')  
    }
    return <div className = 'popUp' > 
    <button className = 'promote-button' style = {{backgroundImage : `url(${wqURL})`}} onClick = {giveQueen}></button>
    <button className = 'promote-button' style = {{backgroundImage : `url(${wnURL})`}} onClick = {giveNight}></button>
    <button className = 'promote-button' style = {{backgroundImage : `url(${wbURL})`}} onClick = {giveBishop}></button>
    <button className = 'promote-button' style = {{backgroundImage : `url(${wrURL})`}} onClick = {giveRook}></button>
    </div>

}
