import React, {useRef, useState } from 'react';
import Tile from '../Tile/Tile';
import './chessboard.css';
import Referee from '../../referee/Referee'
import {VERTICAL_AXIS, HORIZONTAL_AXIS, GRID_SIZE, Piece, TeamType, initialBoardState, GameState, initialGameState} from '../../constants'
import Checker from '../../referee/boardChecks'

export default function Chessboard(){
    const [activePiece, setActivePiece] = useState<HTMLElement | null>(null) 
    const [gridX, setGridX] = useState(0);
    const [gridY, setGridY] = useState(0);
    const [pieces, setPieces]  = useState<Piece[]>(initialBoardState);
    const [gameState, setGameState] = useState<GameState[]>(initialGameState)
    const [activePlayer, setActivePlayer] = useState<TeamType>(TeamType.WHITE);
    const chessboardRef = useRef<HTMLDivElement>(null);
    const referee = new Referee();
    const checker = new Checker();


    function switchPlayer(){
        const nextPlayer = activePlayer === TeamType.WHITE ? TeamType.BLACK : TeamType.WHITE
        setActivePlayer(nextPlayer) 
    }
    function deepCopy() : Piece [] {
        const oldBoardState : Piece[] = [];
        pieces.forEach(p => oldBoardState.push({image : p.image, x : p.x,  y : p.y, team : p.team, type : p.type, enPassantable : p.enPassantable}))
        return oldBoardState
    }

    function revert(){
        if (activePiece){
            activePiece.style.position = 'relative';
            activePiece.style.removeProperty('left');
            activePiece.style.removeProperty('top');
        }
    }

    function grabPiece(e: React.MouseEvent){
        const chessboard = chessboardRef.current
        const element = e.target as HTMLElement
        if (element.classList.contains('chess-piece') && chessboard){
            setGridX(Math.floor((e.clientX - chessboard.offsetLeft)/GRID_SIZE));
            setGridY(Math.abs(Math.ceil((e.clientY - chessboard.offsetTop - 8 * GRID_SIZE)/GRID_SIZE)));
            const x = e.clientX - GRID_SIZE/2;
            const y = e.clientY - -GRID_SIZE/2;
            element.style.position = 'absolute'; 
            element.style.left = `${x}px`;
            element.style.top = `${y}px`;
            setActivePiece(element)    
        }

    }

    function movePiece(e: React.MouseEvent){
        const chessboard = chessboardRef.current
        if (activePiece && chessboard){
            const x = e.clientX - GRID_SIZE/2;
            const y = e.clientY - GRID_SIZE/2;
            activePiece.style.position = 'absolute'; 
            activePiece.style.left = `${x}px`;
            activePiece.style.top = `${y}px`;
        }
    }

    function dropPiece(e: React.MouseEvent){
        const chessboard = chessboardRef.current;
        if (activePiece && chessboard){
            const x = Math.floor((e.clientX - chessboard.offsetLeft)/GRID_SIZE);
            const y = Math.abs(Math.ceil((e.clientY - chessboard.offsetTop - 8 * GRID_SIZE)/GRID_SIZE))
            console.log(x, y, e.clientX, e.clientY)
            const currentPiece = pieces.find(p => p.x === gridX && p.y === gridY)
            if (currentPiece){
                const oldBoardState = deepCopy()
                const [validMove, gameChanger] = referee.isValidMove(gridX, gridY, x, y, currentPiece.type, currentPiece.team, pieces, activePlayer, gameState);                
                if (validMove){
                    setPieces(pieces)
                    const takenPiece = pieces.find(p => p.taken === true)
                    const id1 = pieces.indexOf(currentPiece)
                    currentPiece.x = x
                    currentPiece.y = y
                    pieces[id1] = currentPiece
                    if (takenPiece){
                        const id2 = pieces.indexOf(takenPiece)
                        pieces.splice(id2, 1)
                    }
                    setPieces(pieces)
                    if (checker.isMyKingInCheck(currentPiece.team, pieces)){
                        console.log('here')
                        setPieces(oldBoardState)
                        revert()
                    } else {
                        switchPlayer()
                    }
                } else {
                revert()
                }
            }
        }
        setActivePiece(null);
    }


    let board = [];
    for (let j = VERTICAL_AXIS.length - 1; j >= 0; j-- ){
        for (let i = 0; i < HORIZONTAL_AXIS.length; i++ ) {
            const number = i + j;
            const piece = pieces.find(p => p.x === i && p.y === j) 
            let image = piece ? piece.image : undefined
            board.push(<Tile key = {`${i}, ${j}`} image = {image} number = {number}/>);
        }
    }
    return <div 
    ref = {chessboardRef} 
    onMouseUp = {e => dropPiece(e)} 
    onMouseMove = {e => movePiece(e)} 
    onMouseDown = {e => grabPiece(e)} 
    id = 'chessboard'>{board}</div>;
}
