import { PieceType, TeamType, Piece } from '../constants'


export function setEnPassant(px : number, py : number, boardState : Piece []){
        boardState.forEach(p => {
            if (p.x === px && p.y === py){
                p.enPassantable = true
            } else{
                p.enPassantable = false
            }
        })
    }

export function takeEnPassant(px : number, py : number, boardState : Piece []){
        boardState.forEach(p => {
            if (p.x === px && p.y === py){
                const id = boardState.indexOf(p)
                boardState.slice(id, 1)
            } 
        })
    }

export function checkEnPassant (x : number, y : number, boardState : Piece []) : boolean {
    const piece = boardState.find(p => p.x === x && p.y === y)
    if (piece && piece.enPassantable){
        return piece.enPassantable  
    } return false
}

export function unsetEnPassant( boardState : Piece []){
    boardState.forEach(p => {
            p.enPassantable = false
    })
}
