import { PieceType, TeamType, Piece } from "../constants"
import { setEnPassant , checkEnPassant, unsetEnPassant, pawnIsBlockaded} from "./pawnHelper"

export default class Referee{
    diagonalPathClear(px : number, py : number, dx : number, dy : number, boardState : Piece[]) : boolean {
        for (let i = 1; i < dx -1; i ++){
            const obstructingPiece = boardState.find(p => p.x === x && p.y === y) 
            if (obstructingPiece){
                return false
            }
        }
        return true
    }
    takePiece(x : number, y: number, team : TeamType, boardState : Piece []){
        const piece = boardState.find(p => p.x === x && p.y === y && p.team !== team)  
        if (piece) {
            piece.taken = true
        }
    }
    isFriendlyPiece(x : number, y: number, team : TeamType, boardState : Piece []): boolean {
        const piece = boardState.find(p => p.x === x && p.y === y)
        const occupied =  piece && piece.team === team ? true : false
        return occupied
    }

    endMove(x : number, y: number, team : TeamType, boardState : Piece [], valid : any){
        if (!this.isFriendlyPiece(x, y, team, boardState)){
            this.takePiece(x, y, team, boardState)
            unsetEnPassant(boardState)
            valid.value = true          
        }
    }
    isValidMove(px : number, py : number, x : number, y : number, type : PieceType, 
                team : TeamType, boardState : Piece [], activePlayer : TeamType){
        if (team !== activePlayer){
            return false
        }
        if (px === x && py === y){
            return false
        }
        const enemyTeam = team === TeamType.WHITE ? TeamType.BLACK : TeamType.WHITE
        var valid = { value: false }
        //PAWN LOGIC 
        //May want to put each piece in different function
        if (type === PieceType.PAWN){
            const startingRow = team === TeamType.WHITE ? 1 : 6
            const increment = team === TeamType.WHITE ? 1 : - 1
            console.log(px, py, x, y, increment)
            // Moving Logic
            if ((py === startingRow && px === x) && (py === y - 2 * increment)){
                if (!pawnIsBlockaded(x, y, boardState) && !pawnIsBlockaded(x, y - increment, boardState)){
                    setEnPassant(px, py, boardState)
                    return true
                }
            } 
            if (py === y - increment && px === x){
                if (!pawnIsBlockaded(x, y, boardState)){
                    unsetEnPassant(boardState)
                    return true;
                }
            } 
            // ATTACK LOGIC
            if ((x === px + 1 || x === px -1) && (py === y - increment) && this.isFriendlyPiece(x, y, enemyTeam, boardState)) {
                this.endMove(x, y, team, boardState, valid)
            }
            if ((x === px + 1 || x === px -1) && (py === y - increment) && this.isFriendlyPiece(x, py, enemyTeam, boardState)) {
                if (checkEnPassant(x, py, boardState)){
                    this.endMove(x, py, team, boardState, valid)
                }
            }
        }
        // NIGHT LOGIC 
        if (type === PieceType.NIGHT){
            if (((px === x + 1 || px === x - 1) &&  (py === y + 2 || py === y - 2)) ||
                ((py === y + 1 || py === y - 1) &&  (px === x + 2 || px === x - 2))){
                    this.endMove(x, y, team, boardState, valid)
            }
        }
        // BISHOP LOGIC
        if (type === PieceType.BISHOP){
            const dx = x - px
            const dy = x - py
            if (Math.abs(dx) === Math.abs(dy)){
                this.diagonalPathClear(px, py, dx, dy, boardState)
                this.endMove(x, y, team, boardState, valid)
            }
        }

        return valid.value
    }
}
