            return false
        }
        var valid = { value: false }
        const dx = x - px
        const dy = y - py
        // PAWN LOGIC
        if (type === PieceType.PAWN){
            const increment = team === TeamType.WHITE ? 1 : - 1
            if (isPawnStartingMove(px, py, x, y, team, boardState, increment)){
                setEnPassant(px, py, boardState)
                return true
            }
            if (isPawnNormalMove(px, py, x, y, team, boardState, increment)){
                this.endMove(x, y, team, boardState, valid)
            }
            if (isEnPassant(px, py, x, y, team, boardState, increment)){
                this.endMove(x, py, team, boardState, valid)
            }
        }
        switch(type){
            case PieceType.NIGHT : {
                isLegalNightMove(px, py, dx, dy, boardState);
                break;
            }
            case PieceType.BISHOP : {
                isLegalBishopMove(px, py, dx, dy, boardState);
                break;
            }
            case PieceType.ROOK : {
                isLegalRookMove(px, py, dx, dy, boardState);
                break;
            }
            case PieceType.QUEEN : {
                isLegalQueenMove(px, py, dx, dy, boardState);
                break;
            }
            case PieceType.KING : {
                isLegalKingMove(px, py, dx, dy, boardState);
                break;
            }
        }
        this.endMove(x, y, team, boardState, valid)

        // // NIGHT LOGIC 
