import { PieceType, TeamType } from "./constants"

export default class SuperPiece{
    subPieces : PieceType [] = [];
    team : TeamType | null = null;
    checksLike : PieceType = PieceType.NO_PIECE;
    img_url : string = '';

    constructor (subPieces: PieceType [], team : TeamType | null){
        this.subPieces = subPieces 
        this.team = team
        this.setURL()
        this.setChecksLike()
    }

    setURL(){
            let url = ''
            const dir = this.team === TeamType.WHITE ? 'white_pieces': 'black_pieces';
            for (let i = 0; i < this.subPieces.length; i ++){
                url = url + this.subPieces[i]
                this.img_url = `assets/${dir}/${url}.png`
            }
    }

    equals(arr1 : PieceType [], arr2 : PieceType []) : boolean{
        if (arr1.length !== arr2.length){
            return false
        }
        for (let i = 0; i < arr1.length; i ++){
            if (arr1[i] !== arr2[i]){
                return false
            }
        } 
        return true
    }


    setChecksLike(){
        if (this.subPieces.length === 1){
            this.checksLike = this.subPieces[0]
        } 
        else if (this.equals(this.subPieces, [PieceType.BISHOP, PieceType.PAWN])){
            this.checksLike = PieceType.PAWN
        }
        else if (this.equals(this.subPieces, [ PieceType.QUEEN, PieceType.BISHOP, PieceType.PAWN])){
                this.checksLike = PieceType.PAWN
        }   
        else if (this.equals(this.subPieces, [PieceType.KING, PieceType.QUEEN, PieceType.BISHOP, PieceType.PAWN])){
            this.checksLike = PieceType.PAWN
        }
        else if (this.equals(this.subPieces, [PieceType.KING, PieceType.BISHOP, PieceType.PAWN])){
            this.checksLike = PieceType.PAWN
        } 
        else if (this.equals(this.subPieces, [PieceType.KING, PieceType.QUEEN])){
            this.checksLike = PieceType.KING
        } 
        else if (this.equals(this.subPieces, [PieceType.KING, PieceType.QUEEN, PieceType.ROOK])){
            this.checksLike = PieceType.WIERD_XY
        } 
        else if (this.equals(this.subPieces, [PieceType.QUEEN, PieceType.ROOK])){
            this.checksLike = PieceType.ROOK
        } 
        else if (this.equals(this.subPieces, [PieceType.QUEEN, PieceType.BISHOP])){
            this.checksLike = PieceType.BISHOP
        }  
        else if (this.equals(this.subPieces, [PieceType.KING, PieceType.QUEEN, PieceType.BISHOP])){
            this.checksLike = PieceType.WIERD_D
        }
    }






}
