import React, {useRef, useState } from 'react';
import Tile from '../Tile/Tile';
import './chessboard.css';
import Referee from '../../referee/Referee'


const horizontalAxis = ['a', 'b', 'c', 'd', 'e', 'f',   'g', 'h'];
const verticalAxis = [1,2,3,4,5,6,7,8];

interface Piece {
    image: string;
    x : number; 
    y : number; 
    type : PieceType;
    team : TeamType;
}

export enum TeamType{
    WHITE,
    BLACK
}

export enum PieceType {
    PAWN,
    BISHOP, 
    NIGHT, 
    ROOK,
    QUEEN,
    KING,
}

// Setting Up board starting position
const initialBoardState : Piece[] = [];

for (let p = 0; p < 2; p ++){
    const type = p === 0 ? 'b': 'w';
    const team = p === 0 ? TeamType.BLACK : TeamType.WHITE;
    const y = p === 0 ?  7 : 0;
    // Rooks
    initialBoardState.push({image : `assets/images/rook_${type}.png`, x : 0,  y, team, type : PieceType.ROOK})
    initialBoardState.push({image : `assets/images/rook_${type}.png`, x : 7,  y, team, type : PieceType.ROOK})

    // Nights
    initialBoardState.push({image : `assets/images/night_${type}.png`, x : 1,  y, team, type : PieceType.NIGHT})
    initialBoardState.push({image : `assets/images/night_${type}.png`, x : 6,  y, team, type : PieceType.NIGHT})

    // Bishops
    initialBoardState.push({image : `assets/images/bishop_${type}.png`, x : 2,  y, team, type : PieceType.BISHOP})
    initialBoardState.push({image : `assets/images/bishop_${type}.png`, x : 5,  y, team, type : PieceType.BISHOP})

    // Queens
    initialBoardState.push({image : `assets/images/queen_${type}.png`, x : 3,  y, team, type : PieceType.QUEEN})
    // Kings
    initialBoardState.push({image : `assets/images/king_${type}.png`, x : 4,  y, team, type : PieceType.KING})

}

// Pawns
for (let i = 0; i < horizontalAxis.length; i++){
    initialBoardState.push({image : 'assets/images/pawn_b.png', x : i, y : 6, team : TeamType.BLACK, type : PieceType.PAWN})
}
for (let i = 0; i < horizontalAxis.length; i++){
    initialBoardState.push({image : 'assets/images/pawn_w.png', x : i, y : 1, team : TeamType.WHITE, type : PieceType.PAWN})
}



export default function Chessboard(){
    const [activePiece, setActivePiece] = useState<HTMLElement | null>(null) 
    const [gridX, setGridX] = useState(0);
    const [gridY, setGridY] = useState(0);
    const [pieces, setPieces]  = useState<Piece[]>(initialBoardState);
    const chessboardRef = useRef<HTMLDivElement>(null);
    const referee = new Referee();

    function grabPiece(e: React.MouseEvent){
        const chessboard = chessboardRef.current
        const element = e.target as HTMLElement
        if (element.classList.contains('chess-piece') && chessboard){
            setGridX(Math.floor((e.clientX - chessboard.offsetLeft)/100));
            setGridY(Math.abs(Math.ceil((e.clientY - chessboard.offsetTop - 800)/100)));
            const x = e.clientX - 50;
            const y = e.clientY - 50;
            element.style.position = 'absolute'; 
            element.style.left = `${x}px`;
            element.style.top = `${y}px`;
            setActivePiece(element)    
        }

    }

    function movePiece(e: React.MouseEvent){
        const chessboard = chessboardRef.current
        if (activePiece && chessboard){
            const minX = chessboard.offsetLeft - 25;
            const minY = chessboard.offsetTop - 25;
            const maxX = chessboard.offsetLeft + chessboard.clientWidth -75;
            const maxY = chessboard.offsetTop + chessboard.clientHeight -75;
            const x = e.clientX -50;
            const y = e.clientY -50;
            activePiece.style.position = 'absolute'; 
            if (x < minX) {
                activePiece.style.left = `${minX}px` ;
            } else if (x > maxX) {
                activePiece.style.left = `${maxX}px`;
            } else {
                activePiece.style.left = `${x}px`;
            }
            if (y < minY) {
                activePiece.style.top = `${minY}px`;
            } else if (y > maxY) {
                activePiece.style.top = `${maxY}px`;
            } else {
                activePiece.style.top = `${y}px`;
            }
        }
    }

    function dropPiece(e: React.MouseEvent){
        const chessboard = chessboardRef.current;
        if (activePiece && chessboard){
            const x = Math.floor((e.clientX - chessboard.offsetLeft)/100);
            const y = Math.abs(Math.ceil((e.clientY - chessboard.offsetTop - 800)/100))
            setPieces((value) => {
                const pieces = value.map((p) => {
                    if (p.x === gridX && p.y === gridY) { 
                            referee.isValidMove(gridX, gridY, x, y, p.type, p.team);
                            p.x = gridX;
                            p.y = gridY;
                    }
                    return p
                });
            return pieces})
            setActivePiece(null);
        }  
    }





    let board = [];
    for (let j = verticalAxis.length - 1; j >= 0; j-- ){
        for (let i = 0; i < horizontalAxis.length; i++ ) {
            const number = i + j;
            let image = undefined;
            pieces.forEach(p => {
                if (p.x === i && p.y === j){
                    image = p.image;
                }

            }); 
            board.push(<Tile key = {`${i}, ${j}`} image = {image} number = {number}/>);
        }
    }
    return <div 
    ref = {chessboardRef} 
    onMouseUp = {e => dropPiece(e)} 
    onMouseMove = {e => movePiece(e)} 
    onMouseDown = {e => grabPiece(e)} 
    id = 'chessboard'>{board}</div>;
}
