// TODO
/*
Would be nice to implement a backend and make the game multiplayer across devices
*/

// NOTES
/*
Throughout the code variables with the names px, py are meant to be read as 
previous x, and previous y respectively.
*/


import './App.css';
import CapturedPieces from './components/CapturedPieces/CapturedPieces';
import Chessboard from './components/Chessboard/chessboard';
import { TAKEN_PIECES_BLACK, TAKEN_PIECES_WHITE } from './components/TakePiece/TakePiece';
import { useState, useEffect } from 'react';


function App() {
  const [capturedWhite, setWhiteCaptured] = useState<number[]>(TAKEN_PIECES_WHITE)
  const [capturedBlack, setBlackCaptured] = useState<number[]>(TAKEN_PIECES_BLACK)
    useEffect(() => {
      setWhiteCaptured(TAKEN_PIECES_WHITE)
    }, [TAKEN_PIECES_WHITE])
    useEffect(() => {
      setBlackCaptured(TAKEN_PIECES_BLACK);
    }, [TAKEN_PIECES_BLACK])
  return (
      <div id = 'App'>
        <CapturedPieces capturedWhitePieces = {capturedWhite} capturedBlackPieces = {capturedBlack}></CapturedPieces>
        <Chessboard></Chessboard>
      </div>
  );
}

export default App;
