import { TeamType } from '../../constants'
import PopUp from '../PopUp/PopUp'
import './tile.css'

interface Props {
    number : number;
    isPromotion : boolean;
    image? : string;
}


export default function Tile({number, isPromotion, image}: Props){
    if (number % 2 === 0){
        if (!isPromotion){
            return <div className = 'tile black-tile'>
            {image && <div style = {{backgroundImage : `url(${image})`}} className = 'chess-piece'></div>}
            </div>
        } else {
            return <div className = 'tile black-tile'><PopUp team = {TeamType.WHITE}/></div>
        }
    } else{
        if (!isPromotion){
            return <div className = 'tile white-tile'> 
            {image && <div style = {{backgroundImage : `url(${image})`}} className = 'chess-piece'></div>}
            </div>
        } else {
            return <div className = 'tile black-tile'>{PopUp}</div>
        }
    }

}
