import './PopUp.css'
import React, {useRef, useState, } from 'react';

interface Props {
    trigger? : boolean;
}

export default function PopUp({trigger} : Props){
    return trigger ? 
    <div className = 'popUp'> 
    <h3> 'Congratulations Youve Been Promoted' </h3> 
    <button onClick = {<queenPopUp trigger = {trigger}/>} >Queen Me</button>
    </div> : <div/>

}

function queenPopUp({trigger} : Props){
    return trigger ? 
    <div className = 'Q'> 
    <h3> You chose Queen </h3> 
    </div> : <div/>
}
