import { TAKEN_PIECES_BLACK, TAKEN_PIECES_WHITE } from "../components/TakePiece/TakePiece";
import { Piece, PieceType, TeamType } from "../constants";


const all_pieces = [PieceType.KING, PieceType.QUEEN, PieceType.ROOK, PieceType.BISHOP, PieceType.NIGHT, PieceType.PAWN]


let whiteNumPiece : number [] = [1, 1, 2, 2, 2, 8]
let blackNumPiece : number [] = [1, 1, 2, 2, 2, 8]
let count : number = 0





function collapsed(piece : PieceType, boardState : Piece [], team : TeamType) : number [] {
    let ids : number [] = []
    for(let i = 0; i < boardState.length; i ++){
        if (boardState[i].SuperPiece.subPieces.length === 1 && 
            boardState[i].SuperPiece.subPieces[0] === piece &&
            boardState[i].SuperPiece.team === team){
            ids.push(i)
        }
    }
    return ids
}

function uncollapsed(piece : PieceType, boardState : Piece [], team : TeamType) : number [] {
    let ids : number [] = []
    for(let i = 0; i < boardState.length; i ++){
        if (boardState[i].SuperPiece.subPieces.length > 1 && 
            boardState[i].SuperPiece.subPieces.includes(piece) &&
            boardState[i].SuperPiece.team === team){
            ids.push(i)
        }
    }
    return ids
}

function removeOne(piece : PieceType, boardState : Piece [], team : TeamType){
    for(let i = 0; i < boardState.length; i ++){
        if (boardState[i].SuperPiece.subPieces.length > 1 && 
            boardState[i].SuperPiece.subPieces.includes(piece) && 
            boardState[i].SuperPiece.team === team){
            const id = boardState[i].SuperPiece.subPieces.indexOf(piece)
            boardState[i].SuperPiece.subPieces.splice(id, 1)
            count = count + 1
        }
    }
}

function removeAll(piece : PieceType, boardState : Piece [], team : TeamType){
    for(let i = 0; i < boardState.length; i ++){
        if (boardState[i].SuperPiece.subPieces.length > 1 && 
            boardState[i].SuperPiece.subPieces.includes(piece) && 
            boardState[i].SuperPiece.team === team){
            const id = boardState[i].SuperPiece.subPieces.indexOf(piece)
            boardState[i].SuperPiece.subPieces.splice(0, id)
            boardState[i].SuperPiece.subPieces = [boardState[i].SuperPiece.subPieces[0]]
            count = count + 1
        }
    }
}

function deductiveCollapse(boardState : Piece [], team : TeamType) {
    if (team === TeamType.WHITE){
        for (let i =  0; i < all_pieces.length; i++){
            const ids = uncollapsed(all_pieces[i], boardState, team)
            const ids2 = collapsed(all_pieces[i], boardState, team)
            if (ids2.length + TAKEN_PIECES_WHITE[i] >= whiteNumPiece[i]){
                removeOne(all_pieces[i], boardState, team)               
            }
            if (ids.length + ids2.length + TAKEN_PIECES_WHITE[i] <= whiteNumPiece[i]){
                if (i === 5){
                console.log(ids.length, ids2.length, TAKEN_PIECES_WHITE[i])
                }
                removeAll(all_pieces[i], boardState, team)

            }
        }
    }
    else {
        for (let i =  0; i < all_pieces.length; i++){
            const ids = uncollapsed(all_pieces[i], boardState, team)
            const ids2 = collapsed(all_pieces[i], boardState, team)
            if (ids2.length + TAKEN_PIECES_BLACK[i] >= blackNumPiece[i]){
                removeOne(all_pieces[i], boardState, team)
            }
            if (ids.length + ids2.length + TAKEN_PIECES_BLACK[i] <= blackNumPiece[i]){
                removeAll(all_pieces[i], boardState, team)
            }
        }
    }
}


export default function fullReduction(boardState : Piece []){
    let somethingChanged = true
    while (somethingChanged === true){
        deductiveCollapse(boardState, TeamType.WHITE)
        deductiveCollapse(boardState, TeamType.BLACK)
        somethingChanged = count !== 0
        count = 0
    }
}
