import { PieceType, TeamType } from "../components/chessboard/chessboard"

export default class Referee{
    isValidMove(px : number, py : number, x : number, y : number, type : PieceType, team : TeamType){
        console.log("Ref checking the move .... ")

        // PAWN MOVES LOGIC
        if (type === PieceType.PAWN){
            // WHITE PAWNS
            if (team === TeamType.WHITE){
                if (py === 1 && (py === y - 1 || py === y -2)){
                    return true;
                } else if (py === y - 1){
                    return true;
                } 
            } 
            if (team === TeamType.BLACK){
                if (py === 1 && (py === y + 1 || py === y  + 2)){
                    return true;
                } else if (py === y + 1){
                    return true;
                }
            } 

        }
        return false
    }
}
