import './PopUp.css'
import React, {useRef, useState, } from 'react';

interface Props {
    trigger? : boolean;
}

export default function PopUp({trigger} : Props){
    const [on, setOn] = useState(false)
    if (trigger){
        setOn(trigger)
        trigger = false
    }
    return !on ? 
    <div className = 'popUp'> 
    <h3> 'Congratulations Youve Been Promoted' </h3> 
    <button >Queen Me</button>
    </div> : <div/>

}
