import { PieceType, TeamType } from "./constants"

export default class SuperPiece{
    subPieces : PieceType [] = []
    team : TeamType | null = null
    checksLike : PieceType = PieceType.NO_PIECE
    img_url : string = ''

    constructor (subPieces: PieceType [], team : TeamType | null){
        this.subPieces = subPieces 
        this.team = team
        this.setURL(subPieces, team)
        this.setChecksLike()
    }

    setURL(subPieces: PieceType [], team : TeamType | null){
        if(TeamType){
            let url = ''
            const dir = team === TeamType.WHITE ? 'white_pieces': 'black_pieces';
            for (let i = 0; i < subPieces.length; i ++){
                url = url + subPieces[i]
                this.img_url = `assets/${dir}/${url}.png`
            }
        }
    }

    setChecksLike(){
        if (this.subPieces.length === 1){
            this.checksLike = this.subPieces[0]
        } 
        else if (this.subPieces === [PieceType.BISHOP, PieceType.PAWN]){
            this.checksLike = PieceType.PAWN
        }
        else if (this.subPieces === [ PieceType.QUEEN, PieceType.BISHOP, PieceType.PAWN]){
                this.checksLike = PieceType.PAWN
        }   
        else if (this.subPieces === [PieceType.KING, PieceType.QUEEN, PieceType.BISHOP, PieceType.PAWN]){
            this.checksLike = PieceType.PAWN
        }
        else if (this.subPieces === [PieceType.KING, PieceType.BISHOP, PieceType.PAWN]){
            this.checksLike = PieceType.PAWN
        } 
        else if (this.subPieces === [PieceType.KING, PieceType.QUEEN]){
            this.checksLike = PieceType.KING
        } 
        else if (this.subPieces === [PieceType.KING, PieceType.QUEEN, PieceType.ROOK]){
            this.checksLike = PieceType.WIERD_XY
        } 
        else if (this.subPieces === [PieceType.QUEEN, PieceType.ROOK]){
            this.checksLike = PieceType.ROOK
        } 
        else if (this.subPieces === [PieceType.QUEEN, PieceType.BISHOP]){
            this.checksLike = PieceType.BISHOP
        }  
        else if (this.subPieces === [PieceType.KING, PieceType.QUEEN, PieceType.BISHOP]){
            this.checksLike = PieceType.WIERD_D
        }
    }






}
