import './PopUp.css'
import React, {useRef, useState, } from 'react';
import { PromotionSquare } from '../../constants'

const wqURL = 'assets/images/bishop_b.png'

export default function PopUp({isPromoted, x, y} : PromotionSquare){
    const [display, setDisplay] = useState('Congratulations You Promoted')

    function giveQueen(){
        setDisplay('You Chose Queen')  
    }
    function giveNight(){
        setDisplay('You Chose Night')  
    }
    function giveBishop(){
        setDisplay('You Chose Bishop')  
    }
    function giveRook(){
        setDisplay('You Chose Rook')  
    }
    return isPromoted ? 
    <div className = 'popUp' > 
    <h3> {display} </h3> 
    <button className = 'promoteButton' style = {{backgroundImage : `url(${image})`}} onClick = {giveQueen}>Queen Me</button>
    <button className = 'promoteButton' onClick = {giveNight}>Night Me</button>
    <button className = 'promoteButton' onClick = {giveBishop}>Bishop Me</button>
    <button className = 'promoteButton' onClick = {giveRook}>Rook Me</button>
    </div> : <div/>

}
