import { PieceType, TeamType, Piece } from "../components/chessboard/chessboard"

export default class Referee{
    isOccupied(x : number, y: number, boardState : Piece []): boolean {
        const piece = boardState.find(p => p.x === x && p.y === y) 
        const occupied =  piece ? true : false
        return occupied
    }
    isValidMove(px : number, py : number, x : number, y : number, type : PieceType, team : TeamType, boardState : Piece []){
        console.log("Ref checking the move .... ")

        // PAWN MOVES LOGIC
        if (type === PieceType.PAWN){
            // WHITE PAWNS
            if (team === TeamType.WHITE){
                if ((py === 1 && px === x) && (py === y -2)){
                    if (!this.isOccupied(x, y, boardState) && !this.isOccupied(x, y - 1, boardState)){
                        return true
                    }
                } 
                if (py === y - 1 && px === x){
                    if (!this.isOccupied(x, y, boardState)){
                        return true;
                    }
                } 
            } 
            // BLACK PAWNS
            if (team === TeamType.BLACK){
                if ((py === 6 && px === x) && (py === y + 1 || py === y  + 2)){
                    return true;
                } else if (py === y + 1 && px === x){
                    return true;
                }
            } 

        }
        return false
    }
}
