import './TakenPiece.css'
import { TeamType, Piece } from '../../constants'
import { useState } from 'react';
import SuperPiece from '../../superPiece';



interface Props {
    piece : Piece
}

export default function TakenPiece({piece} : Props) {
    const [chosen, setChosen] = useState<boolean>(true)
    const dir = piece.SuperPiece.team === TeamType.WHITE ? 'white_pieces' : 'black_pieces';
    const top = piece.SuperPiece.team === TeamType.WHITE ? '91vh' : '09vh';
    const left = piece.SuperPiece.team === TeamType.WHITE ? '21vw' : '21vw';
    const qURL = `assets/${dir}/q.png`
    const bURL = `assets/${dir}/b.png`
    const nURL = `assets/${dir}/n.png`
    const rURL = `assets/${dir}/r.png`
    const pURL = `assets/${dir}/p.png`
    let buttons = []
    for (let i = 0; i < SuperPiece.subPieces.length; i++){
        buttons.push(<button className = 'take-button' style = {{backgroundImage : `url(${qURL})`}} onClick = {takeQueen}></button>)
    }
    function takeQueen(){
        setChosen(false)
    }
    function takeRook(){
        setChosen(false)    
    }
    function takeBishop(){
        setChosen(false)       
    }
    function takeNight(){
        setChosen(false)    
    }
    function takePawn(){
        setChosen(false)
    }
    if (chosen){  
    return <span className = 'pop' style = {{top : top, left: left}}> 
    <span className = 'white-text'>Your piece was taken on h3 what piece was it?   </span>
    {buttons}
    </span>
    }
    else {
        return <div></div>
    }
}
